package com.mycompany.study.ch02;

public class ExPromotion2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte byteVar1 = 10;
		System.out.println("byteVar1:" + byteVar1);
		
		short shortVar1 = byteVar1;
		System.out.println("shortVar1:" + byteVar1);
		
		int intVar1 = shortVar1;
		System.out.println("intVar1:" + shortVar1);
		
		long longVar1 = intVar1;
		System.out.println("longVar1:" + intVar1);
		
		float floatVar1 = longVar1;
		System.out.println("floatVar1:" + longVar1);
		
		double doubleVar1 = floatVar1;
		System.out.println("doubleVar1:" + floatVar1);
		
		System.out.println("---------------------------");
		
		byte byteVar2 = 10;
		System.out.println("byteVar2:" + byteVar2);
		
		short shortVar2 = byteVar2;
		System.out.println("shortVar2:" + shortVar2);
		
		int intVar2 = shortVar2;
		System.out.println("intVar2:" + intVar2);
		
		long longVar2 = intVar2;
		System.out.println("longVar2:" + longVar2);
		
		float floatVar2 = longVar2;
		System.out.println("floatVar2:" + floatVar2);
		
		double doubleVar2 = floatVar2;
		System.out.println("doubleVar2:" + doubleVar2);
		
		
		
		
		
		

	}

}
